=> "",							"heading" => __("主要文本颜色", "smile"),							"param_name" => "color_txt_main",							"value" => "",							"description" => __("选择正常的背景颜色.", "smile"),							"dependency" => Array("element" => "color_scheme","value" => array("custom")),						),						array(							"type" => "colorpicker",							"class" => "",							"heading" => __("配置高亮的背景颜色 ", "smile"),							"param_name" => "color_bg_highlight",							"value" => "",							"description" => __("选择突出显示的背景颜色.", "smile"),							"dependency" => Array("element" => "color_scheme","value" => array("custom")),						),						array(							"type" => "colorpicker",							"class" => "",							"heading" => __("强调文本颜色", "smile"),							"param_name" => "color_txt_highlight",							"value" => "",							"description" => __("选择突出显示的背景颜色.", "smile"),							"dependency" => Array("element" => "color_scheme","value" => array("custom")),						),						array(							"type" => "textfield",							"class" => "",							"heading" => __("包名字/标题", "smile"),							"param_name" => "package_heading",							"admin_label" => true,							"value" => "",							"description" => __("输入包名称或表标题", "smile"),						),						array(							"type" => "textfield",							"class" => "",							"heading" => __("副标题 ", "smile"),							"param_name" => "package_sub_heading",							"value" => "",							"description" => __("为这个包输入简短描述", "smile"),						),						array(							"type" => "textfield",							"class" => "",							"heading" => __("组合价格 ", "smile"),							"param_name" => "package_price",							"value" => "",							"description" => __("进入这种包的价格. 例. $157", "smile"),						),						array(							"type" => "textfield",							"class" => "",							"heading" => __("价格单位 ", "smile"),							"param_name" => "package_unit",							"value" => "",							"description" => __("进入这个包价格单位. 例. per month", "smile"),						),						array(							"type" => "textarea_html",							"class" => "",							"heading" => __("特点 ", "smile"),							"param_name" => "content",							"value" => "",							"description" => __("使用无序的列表元素创建特性列表.", "smile"),						),						array(							"type" => "textfield",							"class" => "",							"heading" => __("按钮文字 ", "smile"),							"param_name" => "package_btn_text",							"value" => "",							"description" => __("输入调用操作按钮的文本", "smile"),						),						array(							"type" => "vc_link",							"class" => "",							"heading" => __("按钮的链接 ", "smile"),							"param_name" => "package_link",							"value" => "",							"description" => __("选择/输入调用操作按钮的链接", "smile"),						),						array(							"type" => "checkbox",							"class" => "",							"heading" => __("", "smile"),							"param_name" => "package_featured",							"value" => array("Make this pricing box as featured" => "enable"),						),					)// params				));// vc_map			}		}		function ultimate_pricing_shortcode($atts,$content = null){			// enqueue js			wp_enqueue_script('ultimate-appear');			if(get_option('ultimate_row') == "enable"){				wp_enqueue_script('ultimate-row-bg',plugins_url('../assets/js/',__FILE__).'ultimate_bg.js');			}			wp_enqueue_script('ultimate-custom');			// enqueue css			wp_enqueue_style('ultimate-animate');			wp_enqueue_style('ultimate-style');			wp_enqueue_style("ultimate-pricing",plugins_url("../assets/css/pricing.css",__FILE__));			$design_style = '';			extract(shortcode_atts(array(				"design_style" => "",			),$atts));			$output = '';			require_once(__ULTIMATE_ROOT__.'/templates/pricing/pricing-'.$design_style.'.php');			$design_func = 'generate_'.$design_style;			$design_cls = 'Pricing_'.ucfirst($design_style);			$class = new $design_cls;			$output .= $class->generate_design($atts,$content);			return $output;		}	} // class Ultimate_Pricing_Table	new Ultimate_Pricing_Table;}